import React, { useState, useEffect } from "react";
import "./App.css";
import Header from "./Components/Header";
import Skills from "./Components/Skills";
import Profile from "./Components/Profile";
import Projects from "./Components/Projects";
import Contact from "./Components/Contact";
import en from "./Data/en";
import tr from "./Data/tr";
import axios from "axios";

function App() {
  const [language, setLanguage] = useState("tr"); // Varsayılan dil Türkçe, isteğe bağlı olarak değiştirebilirsiniz
  const [data, setData] = useState(null);

  useEffect(() => {
    axios
      .get(language === "tr" ? "https://run.mocky.io/v3/d724151f-f62e-4a0a-a608-e8e5a02c431a" : "https://run.mocky.io/v3/89cd7d2a-794a-4af3-af0d-0bddaf92b500")
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error("Veri alınamadı: ", error);
      });
  }, [language]);

  const changeLanguage = () => {
    // Dil değiştikçe burada uygun dil kodunu (örneğin "tr" veya "en") ayarlayabilirsiniz.
    setLanguage(language === "tr" ? "en" : "tr");
  };

  //const data = language === "tr" ? tr : en;

  return (
    <>
      {data.header ? <Header language={language} changeLanguage={changeLanguage} data={data.header} /> : null}
      {data ? <Skills skillsData={data}/> : null}
      {data ? <Profile profileData={data} /> : null}
      {data ?  <Projects projectData={data} /> : null}
      {data ? <Contact contactMessage={data} /> : null}
    </>
  );

}

export default App;
