import React from "react";

const Profile = ({ profileData }) => {
  const basicInfoHeaders = profileData.profile["basic-info"]["basic-info-headers"];
  const basicInfoValues = [
    profileData.profile["basic-info"]["doğum-tarihi"],
    profileData.profile["basic-info"]["ikamet-sehri"],
    profileData.profile["basic-info"]["egitim-durumu"],
    profileData.profile["basic-info"]["tercih-rol"]
  ];
  const basicInfoData = basicInfoHeaders.slice(1, 5).map((header, index) => ({
    header,
    value: basicInfoValues[index],
  }));

  return (
    <div className="bg-custom-blue text-green-100 flex flex-col items-center justify-center p-8">
      <h2 className="text-3xl text-left w-8/12 mb-4 text-custom-green font-bold">{profileData.titles[1]}</h2>
      <div className="flex profile-content w-8/12 items-center">


        <div className="flex flex-col w-1/4">
          <h2 className="col-span-2 text-2xl">{basicInfoHeaders[0]}</h2>
          <div>
              {basicInfoHeaders.slice(1, 5).map((header, index) => (
                <div key={index} className="text-white">
                  {header}
                </div>
              ))}
              {basicInfoValues.map((value, index) => (
                <div key={index} className="text-custom-green">
                  {value}
                </div>
              ))}
          </div>

        </div>


        <div className="profile-img flex-1 flex items-center justify-center">
          <img
            src="https://picsum.photos/300"
            alt="Resim Açıklaması"
            className="w-64 h-64 rounded-lg"
          />
        </div>
        <div className="profile-about-me flex-1 text-center p-4">
          <h2 className="text-3xl mb-4">{profileData.titles[2]}</h2>
          <span>{profileData.profile["about-me"]}</span>
        </div>
      </div>
    </div>
  );
};

export default Profile;
