import React, { useState, useEffect } from "react";
import "./App.css";
import Header from "./Components/Header";
import Skills from "./Components/Skills";
import Profile from "./Components/Profile";
import Projects from "./Components/Projects";
import Contact from "./Components/Contact";
import en from "./Data/en";
import tr from "./Data/tr";
import axios from "axios";

function App() {
  const [language, setLanguage] = useState("tr"); // Varsayılan dil Türkçe, isteğe bağlı olarak değiştirebilirsiniz
  const [data, setData] = useState(null);
  useEffect(() => {
    // Dil değiştiğinde API'ye istek atarak verileri çekin
    axios.get("https://run.mocky.io/v3/d724151f-f62e-4a0a-a608-e8e5a02c431a")
      .then((response) => {
        // API'den gelen veriyi state'e kaydedin
        setData(response.data[language]);
      })
      .catch((error) => {
        // Hata durumunda burada işlem yapabilirsiniz
        console.error("API isteği başarısız oldu: ", error);
      });
  }, [language]);

  const changeLanguage = () => {
    // Dil değiştikçe burada uygun dil kodunu (örneğin "tr" veya "en") ayarlayabilirsiniz.
    setLanguage(language === "tr" ? "en" : "tr");
  };
  if (data === null) {
    // Veriler henüz yüklenmediyse yüklenene kadar bir yükleme gösterebilirsiniz
    return <div>Loading...</div>;
  }
  //const data = language === "tr" ? tr : en;

  return (
    <>
      <Header language={language} changeLanguage={changeLanguage} data={data.header} />
      <Skills skillsData={data}/>
      <Profile profileData={data} />
      <Projects projectData={data} />
      <Contact contactMessage={data} />
    </>
  );
}

export default App;
