import React from "react";
import "./Profile.css";
import Data from "../Data/Data";
import en from "../Data/en";
import tr from "../Data/tr";

const Profile = ({ language }) => {
    //const profileData = Data[0].profile["basic-info"];
    const profileData = language === "tr" ? tr : en;
    profileData = profileData.profile["basic-info"]
    const basicInfoHeaders = profileData["basic-info-headers"];
    const basicInfoValues = [
        profileData["doğum-tarihi"],
        profileData["ikamet-sehri"],
        profileData["egitim-durumu"],
        profileData["tercih-rol"]
    ];


    return (
        <div className="profile">
            <h2>Profile</h2>
            <div className="profile-content">
                <div className="profile-basic-info">
                    <div class="parent">
                        {basicInfoHeaders.map((header, index) => (
                            <div key={index} className={`div${index + 1}`}>
                                <span>{header}</span>
                            </div>
                        ))}
                        {basicInfoValues.map((value, index) => (
                            <div key={index} className={`div${index + 6}`}>
                                <span>{value}</span>
                            </div>
                        ))}
                    </div>
                </div>
                <div className="profile-img">
                    <img src="https://picsum.photos/300" alt="Resim Açıklaması" />
                </div>
                <div className="profile-about-me">
                    <h2>About Me</h2>
                    <span>
                        {Data[0].profile["about-me"]}
                    </span>
                </div>
            </div>
        </div>
    );
};

export default Profile;
