import React, { useState, useEffect } from "react";
import "./App.css";
import Header from "./Components/Header";
import Skills from "./Components/Skills";
import Profile from "./Components/Profile";
import Projects from "./Components/Projects";
import Contact from "./Components/Contact";
import axios from "axios";

function App() {
  const [language, setLanguage] = useState("tr");
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Axios isteği yapmadan önce setLoading ile loading'i true yapın
    setLoading(true);

    axios
      .get(language === "tr" ? "https://run.mocky.io/v3/d724151f-f62e-4a0a-a608-e8e5a02c431a" : "https://run.mocky.io/v3/89cd7d2a-794a-4af3-af0d-0bddaf92b500")
      .then((response) => {
        setData(response.data);
        // Verileri başarıyla aldıktan sonra loading'i false yapın
        setLoading(false);
      })
      .catch((error) => {
        console.error("Veri alınamadı: ", error);
        // Hata durumunda da loading'i false yapın
        setLoading(false);
      });
  }, [language]);

  const changeLanguage = () => {
    setLanguage(language === "tr" ? "en" : "tr");
  };

  if (loading) {
    // Veriler yüklenene kadar yükleme göstermek için
    return <div>Loading...</div>;
  }

  return (
    <>
      <Header language={language} changeLanguage={changeLanguage} data={data.header} />
      <Skills skillsData={data.skills} />
      <Profile profileData={data.profile} />
      <Projects projectData={data.projects} />
      <Contact contactMessage={data.contactMessage} />
    </>
  );
}

export default App;
