import React, { useState, useEffect } from "react";
import "./App.css";
import Header from "./Components/Header";
import Skills from "./Components/Skills";
import Profile from "./Components/Profile";
import Projects from "./Components/Projects";
import Contact from "./Components/Contact";
import en from "./Data/en";
import tr from "./Data/tr";
import axios from "axios";

import { LanguageProvider, useLanguage } from "./Contexts/LanguageContext";

function App() {
  //const [language, setLanguage] = useState(localStorage.getItem("language") || "tr");
  const { language, changeLanguage } = useLanguage(); // Dil değerini ve dil değiştirme fonksiyonunu aldık

  const data = language === "tr" ? tr : en;

  useEffect(() => {
    const savedLanguage = localStorage.getItem("language");
    if (savedLanguage) {
      // Eğer localStorage'ta bir dil kaydedilmişse, onu kullan
      changeLanguage(); // Dil değiştirme fonksiyonunu çağırarak LanguageProvider içindeki değeri güncelle
    }
  }, [changeLanguage]);

  // const changeLanguage = () => {
  //   setLanguage(language === "tr" ? "en" : "tr");
  //   localStorage.setItem("language", language === "tr" ? "en" : "tr");
  // };

  //axios ama çalışmıyor
  const [data1, setData] = useState(null);

    useEffect(() => {
      axios
        .get(language === "tr" 
            ? "https://run.mocky.io/v3/3b19ae2f-fc03-4912-932c-5c5dd959eba4" 
            : "https://run.mocky.io/v3/3b19ae2f-fc03-4912-932c-5c5dd959eba4")
        .then((response) => {
          //console.log(response.data);
          setData(response.data);
        })
        .catch((error) => {
          console.error("Veri alınamadı: ", error);
        });
    }, [language]);

    /////

    const [isDarkMode, setIsDarkMode] = useState(false);

    const toggleDarkMode = () => {
      setIsDarkMode(!isDarkMode);
    };



    const bodyClassName = isDarkMode ? 'dark' : '';

    if (data === null) {
      return <div>Loading...</div>; 
    }
    return (
      <LanguageProvider>

      <>
        <Header

          data={data.header}
          toggleDarkMode={toggleDarkMode}
          bodyClassName={bodyClassName}
        />
        <Skills skillsData={data} bodyClassName={bodyClassName} />
        <Profile profileData={data} bodyClassName={bodyClassName} />
        <Projects projectData={data} bodyClassName={bodyClassName} />
        <Contact contactMessage={data} bodyClassName={bodyClassName} />
      </>
      </LanguageProvider>

    );    
}

export default App;
