import React, { useState, useEffect } from "react";
import "./App.css";
import Header from "./Components/Header";
import Skills from "./Components/Skills";
import Profile from "./Components/Profile";
import Projects from "./Components/Projects";
import Contact from "./Components/Contact";
import en from "./Data/en";
import tr from "./Data/tr";
import axios from "axios";

function App() {
  const [language, setLanguage] = useState(localStorage.getItem("language") || "tr");
  const [data, setData] = useState(null);
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`https://raw.githubusercontent.com/berkanyuce/web-sayfam/main/src/Data/${language}.js`);
        setData(response.data);
      } catch (error) {
        console.error('Veri çekme hatası:', error);
      }
    };

    fetchData();
  }, [language]);

  useEffect(() => {
    const savedLanguage = localStorage.getItem("language");
    if (savedLanguage) {
      setLanguage(savedLanguage);
    }
  }, []);

  const changeLanguage = () => {
    setLanguage(language === "tr" ? "en" : "tr");
    localStorage.setItem("language", language === "tr" ? "en" : "tr");
  };

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  const bodyClassName = isDarkMode ? 'dark' : '';

  if (!data) {
    // Veri yüklenene kadar loading durumunu göster
    return <div>Loading...</div>;
  }

    return (
      <>
        <Header
          language={language}
          changeLanguage={changeLanguage}
          data={data.header}
          toggleDarkMode={toggleDarkMode}
          bodyClassName={bodyClassName}
        />
        <Skills skillsData={data} bodyClassName={bodyClassName} />
        <Profile profileData={data} bodyClassName={bodyClassName} />
        <Projects projectData={data} bodyClassName={bodyClassName} />
        <Contact contactMessage={data} bodyClassName={bodyClassName} />
      </>
    );    
}

export default App;
