import React, { useState, useEffect } from "react";
import "./App.css";
import Header from "./Components/Header";
import Skills from "./Components/Skills";
import Profile from "./Components/Profile";
import Projects from "./Components/Projects";
import Contact from "./Components/Contact";
import axios from "axios";

function App() {
  const [isLoading, setLoading] = useState(true);
  const [language, setLanguage] = useState("tr");
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);

      try {
        const response = await axios.get(
          language === "tr"
            ? "https://run.mocky.io/v3/d724151f-f62e-4a0a-a608-e8e5a02c431a"
            : "https://run.mocky.io/v3/89cd7d2a-794a-4af3-af0d-0bddaf92b500"
        );
        setData(response.data);
      } catch (error) {
        console.error("Veri alınamadı: ", error);
      }

      setLoading(false);
    };

    fetchData();
  }, [language]);

  const changeLanguage = () => {
    setLanguage(language === "tr" ? "en" : "tr");
  };

  if (isLoading) {
    return <div className="App">Loading...</div>;
  }

  if (!data || !data.header) {
    // Veri veya header boşsa hata durumunu ele alın
    return <div className="App">Veriler yüklenirken bir hata oluştu.</div>;
  }

  return (
    <>
      <Header language={language} changeLanguage={changeLanguage} data={data.header} />
      <Skills skillsData={data.skills} />
      <Profile profileData={data.profile} />
      <Projects projectData={data.projects} />
      <Contact contactMessage={data.contactMessage} />
    </>
  );
}

export default App;
