import React from "react";

const Projects = ({ projectData, bodyClassName }) => {
  const backgroundColor = bodyClassName == 'dark' ? "dark:bg-dark-green" : "bg-custom-green"

  return (
    <div className={`{$backgroundColor} flex flex-col items-center justify-around p-8`}>
      <h2 className="text-3xl text-left w-8/12 mb-8 text-custom-blue font-bold">{projectData.titles[3]}</h2>
      <div className="flex projects-content w-3/4 flex-col items-center space-y-4">
        {projectData.projects.map((project, index) => (
          <div className="flex project w-11/12 bg-white rounded-lg" key={index}>
            <div className="project-img w-1/4 h-auto rounded-l-md" style={{ backgroundImage: `url(https://picsum.photos/300)` }}></div>
            <div className="project-details flex-1 p-4 pt-4 pb-4">
              <h3 className="text-2xl text-custom-blue font-bold mb-4">{project["project-header"]}</h3>
              <span>{project["project-content"]}</span>
              <div className="flex project-tags space-x-4 mt-4">
                {project["project-tags"].map((tag, tagIndex) => (
                  <a
                    key={tagIndex}
                    className="px-4 py-2 bg-purple-700 text-white rounded-full m-0"
                  >
                    {tag}
                  </a>
                ))}
              </div>
              <div className="flex project-links">
                {project["project-links"].map((link, linkIndex) => (
                  <a
                    key={linkIndex}
                    href={link.link}
                    className="pr-4 mt-4 ml-0 mr-4"
                  >
                    {link["link-details"]}
                  </a>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Projects;
