import React, { useState } from "react";

const Header = ({ language, changeLanguage, data }) => {
    return (
        <div className="bg-purple-700 text-green-100 h-96 flex flex-col">
            <div className="flex flex-row justify-between p-4 m-0">
                <a className="text-xl">{data.nav.name}</a>
                <div className="flex items-center space-x-4">
                    <span onClick={changeLanguage} className="cursor-pointer">
                        {language === "tr" ? "Dil Değiştir" : "Change Language"}
                    </span>
                    <span>Dark Mode</span>
                </div>
            </div>
            <div className="flex content h-4/5 items-center mx-auto w-3/4">
                <div className="flex content-text flex-col items-start w-3/4 gap-4">
                    <h1 className="text-4xl font-bold">{data["header-content"]["header-title"]}</h1>
                    <a className="text-2xl">{data["header-content"]["header-info"]}</a>
                    <div className="flex content-social">
                        {data["header-content"]["header-social"].map(social => (
                            <a key={social.id} href="" className="content-links bg-white p-2 flex items-center rounded-full">
                                <img src={social["img-link"]} alt="Resim Açıklaması" className="mr-2" />
                                {social["social-name"]}
                            </a>
                        ))}
                    </div>
                </div>
                <div className="header-image w-64 h-64 rounded-lg" style={{ backgroundImage: `url('https://picsum.photos/200')` }}></div>
            </div>
        </div>
    );
};

export default Header;
