// Header.js
import React, { useState } from "react";
import "./Header.css";
import Data from "../Data/Data";
import en from "../Data/en";
import tr from "../Data/tr";


const Header = () => {
    const data = Data[0]["header"]; // Veriyi alın

    const [language, setLanguage] = useState("tr");
    const changeLanguage = (newLanguage) => {
        setLanguage(newLanguage);
    };
    const translations = language === "tr" ? tr : en;

    return (
        <div className="header">
            <div className="header-nav">
                <a>{data.nav.name}</a>
                <div className="swapper">
                    <span onClick={() => changeLanguage("tr")}>{translations.changeLanguage}</span>
                    <span onClick={() => changeLanguage("en")}>{translations.changeLanguage}</span>

                    <span>Türkçe'ye geç</span>
                    <span>Dark Mode</span>
                </div>
            </div>
            <div className="content">
                <div className="content-text">
                    <h1>{data["header-content"]["header-title"]}</h1>
                    <a style={{ margin: 0 }}>{data["header-content"]["header-info"]}</a>
                    <div className="content-social">
                        {data["header-content"]["header-social"].map(social => (
                            <a key={social.id} href="" className="content-links">
                                <img src={social["img-link"]} alt="Resim Açıklaması" />
                                {social["social-name"]}
                            </a>
                        ))}
                    </div>
                </div>
                <div className="header-image"></div>
            </div>
        </div>
    );
};

export default Header;
