import React, { useState, useEffect } from "react";
import "./App.css";
import Header from "./Components/Header";
import Skills from "./Components/Skills";
import Profile from "./Components/Profile";
import Projects from "./Components/Projects";
import Contact from "./Components/Contact";
import axios from "axios";

function App() {
  const [isLoading, setLoading] = useState(true);
  const [language, setLanguage] = useState("tr");
  const [data, setData] = useState({
    header: {},
    skills: [],
    profile: {},
    projects: {},
    contactMessage: ""
  });

  useEffect(() => {
    axios
      .get(language === "tr" ? "https://run.mocky.io/v3/d724151f-f62e-4a0a-a608-e8e5a02c431a" : "https://run.mocky.io/v3/89cd7d2a-794a-4af3-af0d-0bddaf92b500")
      .then((response) => {
        setData(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Veri al覺namad覺: ", error);
        setLoading(false);
      });
  }, [language]);

  const changeLanguage = () => {
    setLanguage(language === "tr" ? "en" : "tr");
  };

  if (isLoading) {
    return <div className="App">Loading...</div>;
  }

  return (
    <>
      <Header language={language} changeLanguage={changeLanguage} data={data.header} />
      <Skills skillsData={data} />
      <Profile profileData={data} />
      <Projects projectData={data} />
      <Contact contactMessage={data} />
    </>
  );
}

export default App;
